function ExpenseDate(props) {
  const expMonth = props.date.toLocaleString("en-UK", { month: "long" });
  const expDay = props.date.toLocaleString("en-UK", { day: "2-digit" });
  const expYear = props.date.getFullYear();

  return (
    <div>
      <div>{expMonth}</div>
      <div>{expYear}</div>
      <div>{expDay}</div>
    </div>
  );
}

export default ExpenseDate;
